import React from 'react';
import {usersFetchData} from "../src/actions/users.js";
import {connect} from "react-redux";
import UsersUpdateForm from "../src/UsersUpdateForm.js"

class UsersListTable extends React.Component {

    render() {

        const handleDeleteUser = id => {
            let answer = window.confirm('Вы уверены, что хотите удалить пользователя?')
            if (answer) {
                fetch('http://localhost:8080/users/' + id,
                    {method: 'DELETE'}).then(result => console.log(result))
            }
        };

        const handleUpdateUser = id => {
                fetch('http://localhost:8080/users/' + id,
                    {method: 'DELETE'}).then(result => console.log(result))
        };

        return (
            <div className="users-list-table">
                <table className="table table-hover">
                    <thead>
                    <tr>
                        <th scope="col">ID</th>
                        <th scope="col">Имя</th>
                        <th scope="col">Возраст</th>
                        <th scope="col">Страна</th>
                        <th scope="col">Действия</th>
                    </tr>
                    </thead>
                    <tbody>
                    {this.props.users.map((user => {
                        return <tr key={user.id}>
                            <th className="id-user-td" scope="row" >{user.id}</th>
                            <td className="name-td">{user.name}</td>
                            <td className="age-td">{user.age}</td>
                            <td className="country-td">{user.country}</td>
                            <td className="actions-td">
                                <div>
                                    <ion-icon name="create" id="edit-user-icon" data-toggle="modal" data-target="#edit-user-modal"
                                              data-whatever="edit-user"/>
                                </div>
                                <div className="delete-user-icon">
                                    <ion-icon name="close" id="delete-user-icon" onClick={() => handleDeleteUser(user.id)}/>
                                </div>
                            </td>
                        </tr>
                    }))
                    }
                    <tr>
                        <th></th>
                        <th></th>
                        <th></th>
                        <th></th>
                        <th></th>
                    </tr>
                    </tbody>
                </table>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        users: state.users
    };
};

const mapDispatchToProps = dispatch => {
    return {
        fetchData: url => dispatch(usersFetchData(url))
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(UsersListTable);
