import React from 'react';
import '../containers/App.css';
import {connect} from "react-redux"
import {usersFetchData} from "../actions/users.js";
import userForm from "../userForm";

// import {userForm} from "../userForm.js";

class App extends React.Component {
    componentDidMount() {
        this.props.fetchData("http://localhost:8080/users");
    }

    render() {
        return (
            <ul>
                <ul>
                    {this.props.users.map((user, index) => {
                        return <ul key={index}>
                            <li>Name is: {user.name}, Age is: {user.age}, Country is: {user.country}</li>
                        </ul>
                    })
                    }
                </ul>
                {/*<div>
                    {userForm}
                </div>*/}
                <div>
                    <h3>Создать пользователя</h3>
                    <form>
                        <input type="text" placeholder="Введите имя" /><br/>
                        <input type="text" placeholder="Введите возраст" /><br/>
                        <input type="text" placeholder="Введите страну" /><br/>
                        <button type="primary" onClick={this.submitHandler}>Создать</button>
                    </form>
                </div>
            </ul>

        );
    }
}

const mapStateToProps = (state) => {
    return {
        users: state.users
    };
};

const mapDispatchToProps = dispatch => {
    return {
        fetchData: url => dispatch(usersFetchData(url))
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
