import React from 'react';
import ScrollTo from '../src/components/ScrollTo';
import banner2 from '../src/images/banner2.jpg';

class UsersBanner extends React.Component {

    constructor() {
        super();
        this.myRef = React.createRef();

        scroll(ref);
        {
            ref.current.scrollIntoView({behavior: 'smooth'})
        }
    }

    render() {

        const bodyLink = document.getElementById("header__body-link");

        return (
            <div className="header">
                <div className="header__banner-container">
                    <img className="header__banner-image" src={banner2} width="960" height="637"
                         alt="Фоновое изображение"/>
                </div>
                <div className="header__banner-image-shadow-glass"/>
                <h2 className="header__head-first-text">Тута типа будут всякие юзеры из мира тырнета</h2>
                <p className="header__line-top">______________________________________________________________________</p>
                <p className="header__line-bottom">______________________________________________________________________</p>
                <h3 className="header__head-second-text">
                    Тута типа будет какойта суперский подзаголовок вашпе полный
                </h3>
                <a className="header__button-scroll-to" href={"#header__body-link"}
                   onClick={() => {this.scroll(this.myRef)}}>Подробнее...</a>
                {/*<p className="header__phone-number-text">8(382)22-65-46</p>
                <p className="header__address-text">г. Томск, пр. Фрунзе, д.103, оф.77</p>*/}
                <p id="header__body-link" ref={this.myRef}/>
            </div>
        );
    }
}

export default UsersBanner;
