import React from 'react';
import '../containers/App.css';
import {connect} from "react-redux"
import {usersFetchData} from "../actions/users.js";
import UsersListTable from "../UsersListTable";
import UsersCreateForm from "../UsersCreateForm";

class App extends React.Component {
    componentDidMount() {
        this.props.fetchData("http://localhost:8080/users");
    }

    render() {
        return (
            <div>
                {/*<div className="App">
                    <ul className="list-group list-group-flush">
                        {this.props.users.map((user, index) => {
                            return <ul key={index} className="list-group-item">
                                <li>Name is: {user.name}, Age is: {user.age}, Country is: {user.country}</li>
                            </ul>
                        })
                        }
                    </ul>
                </div>*/}
                <div className="ListUser">
                    <UsersListTable/>
                </div>
                <div className="CreateUser">
                    <UsersCreateForm/>
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        users: state.users
    };
};

const mapDispatchToProps = dispatch => {
    return {
        fetchData: url => dispatch(usersFetchData(url))
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
