import React from 'react';
import '../containers/App.css';
import {connect} from "react-redux"
import {usersFetchData} from "../actions/users.js";
import userForm from "../userForm";

// import {userForm} from "../userForm.js";

class App extends React.Component {
    componentDidMount() {
        this.props.fetchData("http://localhost:8080/users");
    }

    render() {
        return (
            <ul>
                <ul>
                    {this.props.users.map((user, index) => {
                        return <ul key={index}>
                            <li>Name is: {user.name}, Age is: {user.age}, Country is: {user.country}</li>
                        </ul>
                    })
                    }
                </ul>
                {/*<div>
                    {userForm}
                </div>*/}
                <div className="App">
                    {/*<form>
                        <input type="text" placeholder="Введите имя" /><br/>
                        <br/>
                        <input type="text" placeholder="Введите возраст" /><br/>
                        <br/>
                        <input type="text" placeholder="Введите страну" /><br/>
                        <br/>
                        <button type="primary" onClick={this.submitHandler}>Создать</button>
                    </form>*/}
                    <h3>Создать пользователя</h3>
                    <form>
                        <div className="form-user-name"><br/>
                            <label className="label-name">Имя пользователя</label><br/>
                            <input type="text" className="input-name" placeholder="Введите имя" />
                        </div>
                        <div className="form-user-age"><br/>
                            <label className="label-age">Возраст</label><br/>
                            <input type="text" className="input-age" placeholder="Введите возраст" />
                        </div><br/>
                        <div className="form-user-country">
                            <label className="label-country">Страна</label><br/>
                            <input type="text" className="input-country" placeholder="Введите страну" />
                        </div><br/>
                        <button type="submit" className="btn-user-create">Создать</button>
                    </form>
                </div>
            </ul>

        );
    }
}

const mapStateToProps = (state) => {
    return {
        users: state.users
    };
};

const mapDispatchToProps = dispatch => {
    return {
        fetchData: url => dispatch(usersFetchData(url))
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
