import React from 'react';
import {usersFetchData} from "../src/actions/users.js";
import {connect} from "react-redux";
import UsersUpdateForm from "../src/UsersUpdateForm.js"
import UsersDelete from "./UsersDelete";

class UsersListTable extends React.Component {

    render() {

        const handleDeleteUser = id => {
            // не забываем спросить пользователя,
            // действительно ли он хочет удалить запись
            let answer = window.confirm('Вы уверены, что хотите удалить пользователя?')

            if (answer) {
                fetch('http://localhost:8080/users/' + id,
                    {method: 'DELETE'}).then(result => console.log(result))
            }
        };

        return (
            <div>
                <table className="table table-hover">
                    <thead>
                    <tr>
                        <th scope="col">ID</th>
                        <th scope="col">Имя</th>
                        <th scope="col">Возраст</th>
                        <th scope="col">Страна</th>
                        <th scope="col">Действия</th>
                        <th scope="col"/>
                    </tr>
                    </thead>
                    <tbody>
                    {this.props.users.map((user => {
                        return <tr key={user.id}>
                            <th scope="row" className="user-id">{user.id}</th>
                            <td>{user.name}</td>
                            <td>{user.age}</td>
                            <td>{user.country}</td>
                            <td>
                                <UsersUpdateForm/>
                            </td>
                            <td>
                                <ion-icon name="close" id="delete-user-icon" onClick={() => handleDeleteUser(user.id)}/>
                            </td>
                        </tr>
                    }))
                    }
                    <tr>
                        <th></th>
                        <th></th>
                        <th></th>
                        <th></th>
                        <th></th>
                        <th></th>
                    </tr>
                    </tbody>
                </table>
            </div>
        );
    }

    /*handleDelete(userId) {
        //event.preventDefault();
        fetch('http://localhost:8080/users/' + userId,
            {method: 'DELETE'}).then(result => console.log(result))
    }*/
}

const mapStateToProps = (state) => {
    return {
        users: state.users
    };
};

const mapDispatchToProps = dispatch => {
    return {
        fetchData: url => dispatch(usersFetchData(url))
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(UsersListTable);
