import React from 'react';
import '../containers/App.css';
import {connect} from "react-redux"
import {usersFetchData} from "../actions/users.js";
import userForm from "../userForm";

// import {userForm} from "../userForm.js";

class App extends React.Component {
    componentDidMount() {
        this.props.fetchData("http://localhost:8080/users");
    }

    render() {
        return (
            <div>
                {/*<div>
                    {userForm}
                </div>*/}
                <div className="App">
                    <ul className="list-group list-group-flush">
                        {this.props.users.map((user, index) => {
                            return <ul key={index} className="list-group-item">
                                <li >Name is: {user.name}, Age is: {user.age}, Country is: {user.country}</li>
                            </ul>
                        })
                        }
                    </ul><br/><br/>

                    <button type="button" className="btn btn-primary" data-toggle="modal" data-target="#exampleModal"
                            data-whatever="@getbootstrap">Open modal for @getbootstrap
                    </button>

                    <div className="modal fade" id="exampleModal" tabIndex="-1" role="dialog"
                         aria-labelledby="exampleModalLabel" aria-hidden="true">
                        <div className="modal-dialog" role="document">
                            <div className="modal-content">
                                <div className="modal-header">
                                    <h5 className="modal-title" id="exampleModalLabel">New message</h5>
                                    <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                        <span aria-hidden="true">&times;</span>
                                    </button>
                                </div>
                                <div className="modal-body">
                                    <form>
                                        <div className="form-group">
                                            <label htmlFor="recipient-name"
                                                   className="col-form-label">Recipient:</label>
                                            <input type="text" className="form-control" id="recipient-name" />
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="message-text" className="col-form-label">Message:</label>
                                            <textarea className="form-control" id="message-text"/>
                                        </div>
                                    </form>
                                </div>
                                <div className="modal-footer">
                                    <button type="button" className="btn btn-secondary" data-dismiss="modal">Close
                                    </button>
                                    <button type="button" className="btn btn-primary">Send message</button>
                                </div>
                            </div>
                        </div>
                    </div>

                    <br/><br/>
                    <h3>Создать пользователя</h3>
                    <form>
                        <div className="form-user-name"><br/>
                            <label className="label-name">Имя пользователя</label><br/>
                            <input type="text" className="form-control" id="name" placeholder="Введите имя" />
                        </div>
                        <div className="form-user-age"><br/>
                            <label className="label-age">Возраст</label><br/>
                            <input type="text" className="form-control" id="age" placeholder="Введите возраст" />
                        </div><br/>
                        <div className="form-user-country">
                            <label className="label-country">Страна</label><br/>
                            <input type="text" className="form-control" id="country" placeholder="Введите страну" />
                        </div><br/>
                        <button type="submit" className="btn btn-primary" id="btn-create-user">Создать</button>
                    </form>
                    <br/><br/><br/><br/><br/><br/><br/>
                </div>
            </div>

        );
    }
}

const mapStateToProps = (state) => {
    return {
        users: state.users
    };
};

const mapDispatchToProps = dispatch => {
    return {
        fetchData: url => dispatch(usersFetchData(url))
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
